<?phpnamespace module\tables\object;use html\node;use object\flight;class result_pilot extends result {    function make_table(league_table $data) {        $html = node::create('div.table_wrapper', [],            node::create('h3', [], $data->Title) .            node::create('table.main.results', [],                node::create('thead tr', [],                    node::create('th', ['style' => 'width:42px'], 'Flight No') .                    node::create('th', ['style' => 'width:63px'], 'Date') .                    node::create('th', ['style' => 'width:90px'], 'Club') .                    node::create('th', ['style' => 'width:95px'], 'Glider') .                    node::create('th', ['style' => 'width:60px'], 'Score') .                    node::create('th', ['style' => 'width:298px'], 'Flight Waypoints')                ) .                $data->flights->iterate_return(                    function (flight $flight) {                        return node::create('tr', [],                            node::create('td', [], $flight->fid) .                            node::create('td', [], $flight->date) .                            node::create('td', [], $flight->c_name) .                            node::create('td', [], $flight->g_name) .                            $flight->to_print() .                            node::create('td', [], $flight->coords)                        );                    }                )            )        );        return $html;    }}