<?phpnamespace tables;class result_pilot extends result {    function make_table(league_table $data) {        $inner = '';        //$data->flights->iterate(function ($flight) use (&$inner) {        /* @var \flight $flight */        foreach ($data->flights as $flight) {            $inner .= '            <tr>                <td>' . $flight->fid . '</td>                <td>' . $flight->date . '</td>                <td>' . $flight->c_name . '</td>                <td>' . $flight->g_name . '</td>                ' . $flight->to_print()->get() . '                <td>' . $flight->coords . '</td>            </tr>';        }        //    });        $html = '    <div class="table_wrapper"><h3>' . $data->Title . '</h3>        <table class="main results"><thead><tr>            <th style="width:42px">Flight No</th>            <th style="width:63px">Date</th>            <th style="width:90px">Club</th>            <th style="width:95px">Glider</th>            <th style="width:60px">Score</th>            <th style="width:298px">Flight Waypoints</th></tr></thead>            ' . $inner . '        </table>    </div>';        return $html;    }}