<?phpclass result_list extends result {    function make_table(league_table $data) {        $html = '<div class="table_wrapper"><h3>' . $data->Title . '</h3><table class="Pilot main results"><thead><tr>    <th style="width:45px">Flight No</th>    <th style="width:60px">Name</th>    <th style="width:60px">Date</th>    <th style="width:90px">Club</th>    <th style="width:95px">Glider</th>    <th style="width:60px">Score</th>    <th style="width:298px">Flight Waypoints</th></tr></thead>';        /** @var flight $flight */        foreach ($data->flights as $flight) {            $html .= '        <tr>            <td>' . $flight->fid . '</td>            <td>' . $flight->p_name . '</td>            <td>' . $flight->date . '</td>            <td><a>' . $flight->c_name . '</a></td>            <td>' . $flight->g_name . '</td>            ' . $flight->to_print()->get() . '            <td>' . $flight->coords . '</td>        </tr>';        }        $html .= '</table></div>';        return $html;    }}